$(document).ready(function () {

    var kategoriSec = $("#kategoriSec");
    var urunSec = $("#urunSec");

    kategoriSec.change(function () {

        var katId = $("#kategoriSec").val();

        $.ajax({
            url: "/Admin/Urun/ListByCategory",
            type: "POST",
            dataType: 'json',

            data: { id: katId },
            success: function (data) {
                urunSec.html('');
                $.each(data, function (k, v) {
                    urunSec.append('<option value=' + v["UrunId"] + '>' + v["Ad"] + '</option>')
                })
            },
            error: function (hata, ajaxOptions, thrownError) {

            }

        })
    })
})
